import React from "react";
import { Link } from "react-router-dom";
import { navItems } from "../../utils/constants";


interface NavItemProps {
  item: string;
  path: string;
  onPageChange: (newValue: string) => void;
}

const NavItem: React.FC<NavItemProps> = ({ item, onPageChange }) => {
  return (
    <Link to={navItems.path} className="nav-item btn btn-danger" onClick={() => onPageChange(item)} >
      {item}
    </Link>
  );
};

export default NavItem;
